{
    "serviceGroupName": "Datacenter Services",
    "serviceName": "Infrastructure as Service (IaaS)",
    "productDescription": {
        "description": "Infrastructure as a Service provides bundled compute and Operating system capabilities on pay-per-use basis. This service offers you scalable and highly available compute capabilities without the large capital expenses incurred in owning Server infrastructure.",
        "keyPoints": ["Virtualized compute capability with committed high availability.", "Standard operating system capabilities (Windows/Linux).", "IaaS is integrated with services such as Storage as Service, Server Management service to provide a complete, secure solution for computing and storage across a wide range of applications.", "Offers standard and customized configurations.", "High agility with on demand scalable options", "Highly secured infrastructure with secure multi-tenancy features", "Guaranteed high levels of Server uptime in terms with proactive automated monitoring.", "24X7 ITIL based helpdesk support.", "ITIL/ITSM based standardization, automation, and best practices to support operations."]
    },
    "serviceScope": ["Service scope includes all Aditya Birla Group Companies that are connected to AVBIH network."],
    "prerequisite_Dependency": ["NA"],
    "featureList": [
        {
          "featureName": "Administration Services",
          "deliverables": ["Provision, build and configure new server infrastructure.", "Add, modify, and delete System configuration.", "Server Compute Resource Management."]
        },
        {
          "featureName": "Maintenance and Support Services",
          "deliverables": ["Provide 24X7 ITIL based helpdesk to manage incidents, changes, and troubleshoot problems.", "Provide incident management support which includes:", "Call logging, tracking, and reporting.", "Incident escalation and circumvention according to documented SOP.", "Employ standardized change management processes and procedures for prompt and efficient handling of all changes.", "Provide problem management for failures preventing incident recurrence through comprehensive RCA and permanent fixes"]
        },
        {
          "featureName": "Monitoring and Alerting Services",
          "deliverables": ["Monitor server utilizations."]
        },
        {
          "featureName": "Reporting Services",
          "deliverables": ["Service availability", "Call statistics and incident reports."]
        },
        {
          "featureName": "Value Added Services",
          "deliverables": ["SLA Based delivery & request provisioning.", "24 X 7 monitoring and administration by the skilled resources", " ITIL framework based ticketing tool which helps to track request and changes.", "Deployment and Configuration changes of the instances i.e., provisioning of new virtual machines and configuration changes on existing virtual machines", "Data transfer in BMCSL cloud towards Intranet i.e., in AVBIH network is complimentary. Data transfer 'in' and 'out', refers to application data which is sent in and out of the BMCSL Private cloud towards WAN (AVBIH).", "Operating system license for Windows and Redhat Linux operating system I already included as part server. Customer may not need to bring separate licenses for these operating systems."]
        }
    ],
    "serviceExclusions": ["Application Feasibility on Virtual Environment.", "Application performance tuning.", "Application/OS Cluster Configuration.", "Application-Level Redundancy.", "Operating system/Application version upgrades."],
    "serviceDelivery": {
        "serviceInitiation": "To avail this service e-mail request to abg.servicedesk@adityabirla.com",
        "serviceSupport": "To log problems and issues with this product access the provided Ticket logging portal."
    },
    "serviceLevelSpecifications": [
        {
          "serviceParameter": "Service Availability",
          "serviceLevelDescription": ["Service targets Server Infrastructure availability of 99.5% (less than 4 hours of downtime monthly)."]
        },
        {
          "serviceParameter": "Service Provisioning",
          "serviceLevelDescription": ["Service targets to provision infrastructure within 4 business days of receiving request from the customer. This is applicable as per Plans offered in Service Order Form."]
        },
        {
          "serviceParameter": "Service Response & Resolution",
          "serviceLevelDescription": ["Service ensures response time commitments for incidents are met 99% of the time for all incidents and service requests. Service response time commitments for incidents are categorized by severity of incidents.", "For severity 1 incidents, response time will be within 30 minutes.", "For severity 2 incidents, response time will be within 45 minutes.", "For severity 3 incidents, response time will be within 60 minutes.", "For all service requests, response time will be within 60 minutes"]
        }
    ],
    "baseCost": 50000,
    "billingType": "Monthly",
    "serviceStatus": "Active"
}

